con:javaScripts.con:entry:
var a = 0;

function login() {
document.getElementById('okta-signin-username').value = "abc@example.com";
document.getElementById('okta-signin-password').value = "123";
document.getElementById('okta-signin-submit').click();
}

function waitLogin() {
    if (document.getElementById('okta-signin-username')) {
        setTimeout(login, 10);
    } else {
        if (a++ < 5) {
            setTimeout(waitLogin, 1000);
        }
    }
}

